// CarManagement.js
import React, { useState, useEffect } from 'react';
import Home from '../pages/Home';
import TabelaCars from '../components/tabelaCars';

function CarManagement() {
  const [filterType, setFilterType] = useState("");
  const [filterName, setFilterName] = useState("");
  const [cars, setCars] = useState([]); // Initialize cars state

  const handleFilterTypeChange = (value) => {
    setFilterType(value);
  };

  const handleFilterNameChange = (value) => {
    setFilterName(value);
  };

  useEffect(() => {
    // Fetch your data here (replace 'your-api-endpoint' with your actual API endpoint)
    fetch("your-api-endpoint")
      .then((res) => res.json())
      .then((data) => {
        console.log("Fetched data:", data);
        setCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []); // Empty dependency array to run the effect only once

  useEffect(() => {
    // Filter cars based on user input
    setCarsFiltered(
      cars.filter((car) => {
        if (filterType && car.type !== filterType) {
          return false;
        }
        if (filterName && car.name !== filterName) {
          return false;
        }
        return true;
      })
    );
  }, [filterType, filterName, cars]);

  return (
    <>
      <Home
        filterType={filterType}
        filterName={filterName}
        handleFilterTypeChange={handleFilterTypeChange}
        handleFilterNameChange={handleFilterNameChange}
      />
      <TabelaCars
        filterType={filterType}
        filterName={filterName}
        handleFilterTypeChange={handleFilterTypeChange}
        handleFilterNameChange={handleFilterNameChange}
        carsFiltered={carsFiltered || []}
      />
    </>
  );
}

export default CarManagement;
