// CarManagement.js
import React, { useState, useEffect } from 'react';
import Home from '../pages/Home';
import TabelaCars from '../components/tabelaCars';

function CarManagement() {
  const [filterType, setFilterType] = useState("");
  const [filterName, setFilterName] = useState("");

  const handleFilterTypeChange = (value) => {
    setFilterType(value);
  };

  const handleFilterNameChange = (value) => {
    setFilterName(value);
  };
  useEffect(() => {
    setCarsFiltered(
      cars.filter((car) => {
        if (filterType && car.type !== filterType) {
          return false;
        }
        if (filterName && car.name !== filterName) {
          return false;
        }
        return true;
      })
    );
  }, [filterType, filterName, cars]);
  return (
    <>
      <Home
        filterType={filterType}
        filterName={filterName}
        handleFilterTypeChange={handleFilterTypeChange}
        handleFilterNameChange={handleFilterNameChange}
      />
      <TabelaCars
        filterType={filterType}
        filterName={filterName}
        handleFilterTypeChange={handleFilterTypeChange}
        handleFilterNameChange={handleFilterNameChange}
        carsFiltered={carsFiltered || []}  // Ensure it's not undefined
      />
    </>
  );
};


export default CarManagement;
