import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Car from "./Car";
import UpdateCarModal from "./UpdateCarModal";
import "./App.css";

function App() {
  const [cars, setCars] = useState([]);
  const [tipCars, setTipCars] = useState([]);
  const [newCar, setNewCar] = useState({
    name: "",
    model: "",
    type: "",
  });
  const [modal, setModal] = useState(false);

  const handleDelete = (carId) => {
    // Delete the car on the server
    fetch(`http://127.0.0.1:5000/api/cars/${carId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error deleting car:", error));

    // Update the local state to reflect the deletion
    setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCar((prevNewCar) => ({ ...prevNewCar, [name]: value }));
  };

  const handleAddCar = () => {
    // Add the new car on the server
    fetch("http://127.0.0.1:5000/api/cars", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: newCar.name,
        model: newCar.model,
        type: newCar.type,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error adding car:", error));

    // Update the local state to reflect the addition
    setCars((prevCars) => [...prevCars, newCar]);

    // Clear the form after adding the car
    setNewCar({
      name: "",
      model: "",
      type: "",
    });
  };

  const handleEdit = (car) => {
    // Logic to handle opening the modal for editing
    console.log("Editing car:", car);
    // Add your modal opening logic here
    toogleModal(); // Assuming toogleModal is your modal opening function
  };

  const toogleModal = () => {
    // Toggle the modal state
    setModal(!modal);
  };

  if (!cars || cars.length === 0) {
    return <div>Ni podatkov</div>;
  }

  return (
    <main>
      <div className="container" style={{ textAlign: "center" }}>
        {/* ... (other parts of your component) */}
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Name</th>
              <th>Model</th>
              <th>Tip</th>
              <th>Akcija</th>
            </tr>
          </thead>
          <tbody>
            {cars.map((car, index) => (
              <Car
                key={index}
                car={car}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            ))}
          </tbody>
        </table>
      </div>
      <div className="modal">
        <div className={`overlay ${modal ? "open" : ""}`}>
          <h2>Spremeni avto</h2>
          <p>Test</p>
          <button className="close-modal" onClick={toogleModal}>
            Close
          </button>
        </div>
      </div>
    </main>
  );
}

export default App;
