import React, { useState } from "react";
import "./App.css"; // Make sure to include your CSS file

const Car = ({ car, onUpdate, onDelete }) => {
  const [updatedCar, setUpdatedCar] = useState({ name: "", model: "", type: "" });
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedCar((prevCar) => ({ ...prevCar, [name]: value }));
  };

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    // Clear the input fields after closing the modal
    setUpdatedCar({ name: "", model: "", type: "" });
  };

  const handleUpdate = () => {
    onUpdate(car.id, updatedCar);
    // Clear the input fields after update
    setUpdatedCar({ name: "", model: "", type: "" });
    // Close the modal after update
    setIsModalOpen(false);
  };

  return (
    <tr>
      <td>{car.name}</td>
      <td>{car.model}</td>
      <td>{car.type}</td>
      <td>
        <div className="d-flex">
          <button className="btn btn-warning mr-2" onClick={handleOpenModal}>
            Update
          </button>
          <button className="btn btn-danger" onClick={() => onDelete(car.id)}>
            Delete
          </button>
        </div>

        {/* Modal */}
        {isModalOpen && (
          <div className="modal-overlay">
            <div className="modal">
              <span className="close" onClick={handleCloseModal}>&times;</span>
              <p>
                <label>
                  New Name:
                  <input
                    type="text"
                    name="name"
                    value={updatedCar.name}
                    onChange={handleInputChange}
                  />
                </label>
              </p>
              <p>
                <label>
                  New Model:
                  <input
                    type="text"
                    name="model"
                    value={updatedCar.model}
                    onChange={handleInputChange}
                  />
                </label>
              </p>
              <p>
                <label>
                  New Type:
                  <input
                    type="text"
                    name="type"
                    value={updatedCar.type}
                    onChange={handleInputChange}
                  />
                </label>
              </p>
              <button className="btn btn-warning" onClick={handleUpdate}>
                Save Changes
              </button>
            </div>
          </div>
        )}
      </td>
    </tr>
  );
};

export default Car;
