// CarManagement.js
import React, { useState, useEffect } from 'react';
import Home from '../pages/Home';
import TabelaCars from '../components/tabelaCars';

function CarManagement() {
  const [filterType, setFilterType] = useState("");
  const [filterName, setFilterName] = useState("");
  const [cars, setCars] = useState([]); // Add this state

  const handleFilterTypeChange = (value) => {
    setFilterType(value);
  };

  const handleFilterNameChange = (value) => {
    setFilterName(value);
  };

  useEffect(() => {
    // Pass the setCars function to Home.js so that it can update the state in CarManagement.js
    fetch("http://127.0.0.1:5000/api/cars")
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  return (
    <>
    
      <TabelaCars
        filterType={filterType}
        filterName={filterName}
        handleFilterTypeChange={handleFilterTypeChange}
        handleFilterNameChange={handleFilterNameChange}
        cars={cars} // Pass the cars data
      />
    </>
  );
}

export default CarManagement;
