import './App.css';
import TaskForm from './TaskForm';
import Task from './Task';
import { useEffect, useState } from 'react';
function App() {
  const [tasks, setTasks] = useState([]);
  useEffect(() => {
    if(tasks.length === 0) return;
       localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);
  useEffect(() => {
    const tasks = JSON.parse(localStorage.getItem('tasks'));
    setTasks(tasks);
  },[])
  function addTask(name){
    setTasks((prev) => {
      return [...prev, {name:name,done:false}];
    })
  }
  function updateTaskDone(taskIndex, newDone) {
    setTasks((prev) => {
      if (!Array.isArray(prev) || taskIndex < 0 || taskIndex >= prev.length) {
        // Handle invalid taskIndex or non-array case
        console.error("Invalid taskIndex or non-array");
        return prev;
      }

      const newTasks = [...prev];
      const taskToUpdate = newTasks[taskIndex];

      if (taskToUpdate) {
        // Check if the task is defined before updating 'done'
        taskToUpdate.done = newDone;
      } else {
        console.error("Task at taskIndex is undefined");
      }

      return newTasks;
    });
  }
  return (
      <main>
       <TaskForm onAdd={addTask}/>
       {tasks.map((task, index) => (
  <Task key={index} {...task} onToggle={(done) => updateTaskDone(index,done)} />

))}



      </main>
  );
}

export default App;
