import React, { useState, useEffect } from "react";

const UpdateCarModal = ({ car, onUpdate, onClose }) => {
  const [updatedCar, setUpdatedCar] = useState({});

  useEffect(() => {
    // Set the initial values for the form based on the car data
    setUpdatedCar({ ...car });
  }, [car]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedCar((prevUpdatedCar) => ({ ...prevUpdatedCar, [name]: value }));
  };

  const handleUpdateButtonClick = () => {
    onUpdate(updatedCar.id, updatedCar);
    onClose();
  };

  return (
    <div className="modal" style={modalStyle}>
      <div className="modal-content">
        <h2>Update Car</h2>
        {/* Form for updating the car */}
        <label>Name:</label>
        <input
          type="text"
          name="name"
          value={updatedCar.name || ""}
          onChange={handleInputChange}
        />
        <br />
        <label>Model:</label>
        <input
          type="text"
          name="model"
          value={updatedCar.model || ""}
          onChange={handleInputChange}
        />
        <br />
        <label>Type:</label>
        <input
          type="text"
          name="type"
          value={updatedCar.type || ""}
          onChange={handleInputChange}
        />
        <br />
        <button onClick={handleUpdateButtonClick}>Update Car</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

const modalStyle = {
  display: "block", /* Keep the modal visible */
  position: "fixed",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  backgroundColor: "#fefefe",
  padding: "20px",
  borderRadius: "8px",
  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
};

export default UpdateCarModal;
