import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import Car from "./Car";
import "./App.css";
import Home from "./Home";

function App() {
  const [cars, setCars] = useState([]);
  const [carsFiltered, setCarsFiltered] = useState([]);
  const [tipCars, setTipCars] = useState([]);
  const [nameCars, setNameCars] = useState([]);
  const [filterType, setFilterType] = useState("");
  const [filterName, setFilterName] = useState("");
  const [newCar, setNewCar] = useState({ name: "", model: "", type: "" });
  const [editCar, setEditCar] = useState(null);
  const [modal, setModal] = useState(false);
  const [modalFormData, setModalFormData] = useState({ name: "", model: "", type: "" });

  useEffect(() => {
    setCarsFiltered(
      cars.filter((car) => {
        if (filterType && car.type !== filterType) {
          return false;
        }
        if (filterName && car.name !== filterName) {
          return false;
        }
        return true;
      })
    );
  }, [filterType, filterName, cars]);

  const fetchData = (url, setState) => {
    fetch(url)
      .then((res) => res.json())
      .then((data) => {
        setState(data);
      })
      .catch((error) => console.error("Error:", error));
  };

  useEffect(() => {
    fetchData("http://127.0.0.1:5000/api/cars", setCars);
  }, []);

  useEffect(() => {
    fetchData("http://127.0.0.1:5000/api/name", setNameCars);
  }, []);

  useEffect(() => {
    fetchData("http://127.0.0.1:5000/api/tip", setTipCars);
  }, []);

  const handleDelete = (carId) => {
    fetch(`http://127.0.0.1:5000/api/cars/${carId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error deleting car:", error));

    setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCar((prevNewCar) => ({ ...prevNewCar, [name]: value }));
  };

  const handleAddCar = () => {
    fetch("http://127.0.0.1:5000/api/cars", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newCar),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error adding car:", error));

    setCars((prevCars) => [...prevCars, newCar]);
    setNewCar({ name: "", model: "", type: "" });
  };

  const handleEdit = (car) => {
    setEditCar(car);
    setModalFormData({ name: car.name, model: car.model, type: car.type });
    toogleModal();
  };

  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setModalFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const toogleModal = () => {
    setModal((prevModal) => !prevModal);
  };

  const handleFilterTypeChange = (e) => {
    setFilterType(e.target.value);
  };

  const handleFilterNameChange = (e) => {
    setFilterName(e.target.value);
  };

  const handleUpdateCar = () => {
    fetch(`http://127.0.0.1:5000/api/cars/${editCar.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(modalFormData),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error updating car:", error));

    setCars((prevCars) =>
      prevCars.map((car) => (car.id === editCar.id ? { ...car, ...modalFormData } : car))
    );

    toogleModal();
    setEditCar(null);
  };

  return (
    <main>
      <div className="container" style={{ textAlign: "center" }}>
        <br />
        <h1 className="test text-center">
          Administracijska stran za prodajo avtomobilov
        </h1>
        <br />
        <form>
          <div className="mb-3">
            <label className="form-label">Name:</label>
            <input
              type="text"
              className="form-control"
              name="name"
              value={newCar.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Model:</label>
            <input
              type="text"
              className="form-control"
              name="model"
              value={newCar.model}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Type:</label>
            <select
              className="form-control"
              name="type"
              value={newCar.type}
              onChange={handleInputChange}
              required
            >
              <option value="" disabled>
                Izberi tip vozila
              </option>
              {tipCars.map((tipcar) => (
                <option key={tipcar.id} value={tipcar.nametip}>
                  {tipcar.nametip}
                </option>
              ))}
            </select>
          </div>
          <button
            className={`btn ${
              newCar.name && newCar.model && newCar.type
                ? "btn-success"
                : "btn-secondary"
            }`}
            onClick={handleAddCar}
            disabled={!newCar.name || !newCar.model || !newCar.type}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="16"
              width="14"
              viewBox="0 0 448 512"
            >
