import React, { useState, useEffect } from "react";

const UpdateCarModal = ({ car, onUpdate, onClose }) => {
  const [updatedCar, setUpdatedCar] = useState({
    name: "",
    model: "",
    type: "",
  });

  useEffect(() => {
    if (car) {
      setUpdatedCar({
        name: car.name,
        model: car.model,
        type: car.type,
      });
    }
  }, [car]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedCar((prevUpdatedCar) => ({ ...prevUpdatedCar, [name]: value }));
  };

  const handleUpdate = () => {
    onUpdate(updatedCar);
    onClose();
  };

  return (
    <div className="modal fade" id="updateCarModal" tabIndex="-1" aria-labelledby="updateCarModalLabel" aria-hidden="true">
      <div className="modal-dialog">
        <div className="modal-content">
          <div className="modal-header">
            <h5 className="modal-title" id="updateCarModalLabel">Update Car Information</h5>
            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" onClick={onClose}></button>
          </div>
          <div className="modal-body">
            <form>
              <div className="mb-3">
                <label className="form-label">Name:</label>
                <input
                  type="text"
                  className="form-control"
                  name="name"
                  value={updatedCar.name}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Model:</label>
                <input
                  type="text"
                  className="form-control"
                  name="model"
                  value={updatedCar.model}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Type:</label>
                <input
                  type="text"
                  className="form-control"
                  name="type"
                  value={updatedCar.type}
                  onChange={handleInputChange}
                  required
                />
              </div>
            </form>
          </div>
          <div className="modal-footer">
            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={onClose}>Close</button>
            <button type="button" className="btn btn-primary" onClick={handleUpdate}>Update Car</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UpdateCarModal;
