import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Car from "./Car";
import Tip from "./Tip";
import "./App.css";

function App() {
  const [cars, setCars] = useState([]);
  const [tips, setTips] = useState([]);
  const [newCar, setNewCar] = useState({
    name: "",
    model: "",
    type: "",
  });

  useEffect(() => {
    fetch("http://127.0.0.1:5000/")
      .then((res) => res.json())
      .then((data) => {
        setCars(data[0]); // Assuming cars data is in the first position of the array
        setTips(data[1]); // Assuming tipvozila data is in the second position of the array
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleUpdate = (carId, updatedCar) => {
    // Update the car on the server
    fetch(`http://127.0.0.1:5000/cars/${carId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedCar),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error updating car:", error));

    // Update the local state to reflect the changes
    setCars((prevCars) =>
      prevCars.map((car) =>
        car.id === carId ? { ...car, ...updatedCar } : car
      )
    );
  };

