import React, { useEffect, useState } from 'react';
import TabelaCars from '../components/tabelaCars';

function CarManagement() {
  const [cars, setCars] = useState([]);
  const [carsFiltered, setCarsFiltered] = useState([]);
  const [tipCars, setTipCars] = useState([]);
  const [nameCars, setNameCars] = useState([]);
  const [filterType, setFilterType] = useState("");
  const [filterName, setFilterName] = useState("");
  const [newCar, setNewCar] = useState({
    name: "",
    model: "",
    type: "",
  });
  const [editCar, setEditCar] = useState(null); // State for editing
  const [modal, setModal] = useState(false);
  const [modalFormData, setModalFormData] = useState({
    name: "",
    model: "",
    type: "",
  });

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/cars")
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/name")
      .then((res) => res.json())
      .then((data) => {
        setNameCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/tip")
      .then((res) => res.json())
      .then((data) => {
        setTipCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  useEffect(() => {
    // Filter cars based on user input
    setCarsFiltered(
      cars.filter((car) => {
        if (filterType && car.type !== filterType) {
          return false;
        }
        if (filterName && car.name !== filterName) {
          return false;
        }
        return true;
      })
    );
  }, [filterType, filterName, cars]);

  const handleDelete = (carId) => {
    // Delete the car on the server
    fetch(`http://127.0.0.1:5000/api/cars/${carId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error deleting car:", error));

    // Update the local state to reflect the deletion
    setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
  };

  // ... (rest of the component remains unchanged)



  return (
    <main>
      <div className="container" style={{ textAlign: "center" }}>
        <br />
        <h1 className="test text-center">
          Administracijska stran za prodajo avtomobilov
        </h1>
        <br />
        <form>
          <div className="mb-3">
            <label className="form-label">Name:</label>
            <input
              type="text"
              className="form-control"
              name="name"
              value={newCar.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Model:</label>
            <input
              type="text"
              className="form-control"
              name="model"
              value={newCar.model}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Type:</label>
            <select
              className="form-control"
              name="type"
              value={newCar.type}
              onChange={handleInputChange}
              required
            >
              <option value="" disabled>
                Izberi tip vozila
              </option>
              {tipCars.map((tipcar) => (
                <option key={tipcar.id} value={tipcar.nametip}>
                  {tipcar.nametip}
                </option>
              ))}
            </select>
          </div>
          <button
            className={`btn ${
              newCar.name && newCar.model && newCar.type
                ? "btn-success"
                : "btn-secondary"
            }`}
            onClick={handleAddCar}
            disabled={!newCar.name || !newCar.model || !newCar.type}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="16"
              width="14"
              viewBox="0 0 448 512"
            >
              <path d="M256 80c0-17.7-14.3-32-32-32s-32 14.3-32 32V224H48c-17.7 0-32 14.3-32 32s14.3 32 32 32H192V432c0 17.7 14.3 32 32 32s32-14.3 32-32V288H400c17.7 0 32-14.3 32-32s-14.3-32-32-32H256V80z" />
            </svg>
          </button>
        </form>
        <div className="row">
          <h2 className="text-uppercase" style={{paddingTop:"25px", paddingBottom:"10px"}}>Filtriraj podatke :</h2>
          <div className="col-6 md" style={{ paddingTop: "5px" }}>
            <select
              className="form-control"
              name="type"
              value={filterName}
              onChange={handleFilterNameChange}
            >
              <option value="">Izberi znamko vozila</option>
              {nameCars.map((namecar) => (
                <option key={namecar.id} value={namecar.name}>
                  {namecar.name}
                </option>
              ))}
            </select>
          </div>
          <div className="col-6 md" style={{ paddingTop: "5px" }}>
            <select
              className="form-control"
              name="type"
              value={filterType}
              onChange={handleFilterTypeChange}
            >
              <option value="">Izberi tip vozila</option>
              {tipCars.map((tipcar) => (
                <option key={tipcar.id} value={tipcar.nametip}>
                  {tipcar.nametip}
                </option>
              ))}
            </select>
          </div>
        </div>

        <table className="table table-striped">
          <thead>
            <tr>
              <th>Name</th>
              <th>Model</th>
              <th>Tip</th>
              <th>Akcija</th>
            </tr>
          </thead>
          <tbody>
            {carsFiltered.map((car, index) => (
              <Car
                key={index}
                car={car}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            ))}
          </tbody>
        </table>
      </div>
      <div
        className={`modal ${modal ? "open" : ""}`}
        style={{ textAlign: "center" }}
      >
        <div className="overlay">
          <br></br>
          <h2>Spremeni avtomobil</h2>
          <br></br>
          <input
            type="text"
            className="form-control"
            name="name"
