import React, { useState } from "react";

const Car = ({ car, onDelete }) => {
  const [isUpdateModalOpen, setIsUpdateModalOpen] = useState(false);
  const [updatedCar, setUpdatedCar] = useState({
    name: car.name,
    model: car.model,
    type: car.type,
  });

  const handleUpdate = () => {
    // Implement the logic to update the car using updatedCar
    console.log("Update logic:", updatedCar);

    // Close the modal after updating
    setIsUpdateModalOpen(false);
  };

  const handleDelete = () => {
    // Implement the logic to delete the car
    console.log("Delete logic:", car.id);
  };

  return (
    <tr>
      <td>{car.name}</td>
      <td>{car.model}</td>
      <td>{car.type}</td>
      <td>
        <div className="btn-group" role="group" aria-label="Basic example">
          <button
            className="btn btn-warning xs"
            onClick={() => setIsUpdateModalOpen(true)}
          >
            {/* Your update icon SVG here */}
          </button>
          <button className="btn btn-danger xs" onClick={handleDelete}>
            {/* Your delete icon SVG here */}
          </button>
        </div>

        {/* Update Modal */}
        {isUpdateModalOpen && (
          <div className="modal-overlay">
            <div className="modal">
              <span className="close" onClick={() => setIsUpdateModalOpen(false)}>
                &times;
              </span>
              <p>
                <label>
                  New Name:
                  <input
                    type="text"
                    name="name"
                    value={updatedCar.name}
                    onChange={(e) => setUpdatedCar({ ...updatedCar, name: e.target.value })}
                  />
                </label>
              </p>
              <p>
                <label>
                  New Model:
                  <input
                    type="text"
                    name="model"
                    value={updatedCar.model}
                    onChange={(e) => setUpdatedCar({ ...updatedCar, model: e.target.value })}
                  />
                </label>
              </p>
              <p>
                <label>
                  New Type:
                  <input
                    type="text"
                    name="type"
                    value={updatedCar.type}
                    onChange={(e) => setUpdatedCar({ ...updatedCar, type: e.target.value })}
                  />
                </label>
              </p>
              <button className="btn btn-warning" onClick={handleUpdate}>
                Save Changes
              </button>
            </div>
          </div>
        )}
      </td>
    </tr>
  );
};

export default Car;
