import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Car from "./Car";
import UpdateCarModal from "./UpdateCarModal";
import "./App.css";

function App() {
  const [cars, setCars] = useState([]);
  const [tipCars, setTipCars] = useState([]);
  const [newCar, setNewCar] = useState({
    name: "",
    model: "",
    type: "",
  });
  const [isUpdateModalOpen, setUpdateModalOpen] = useState(false);
  const [selectedCarForUpdate, setSelectedCarForUpdate] = useState({});

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/cars")
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);
  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/tip")
      .then((res) => res.json())
      .then((data) => {
        setTipCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

const handleUpdate = (carId, updatedCar) => {
  // Open the update modal
  handleUpdateModalOpen(updatedCar);

  // The rest of the update logic remains the same
  try {
    fetch(`http://127.0.0.1:5000/api/cars/${carId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedCar),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error updating car:", error));

    // Update the local state to reflect the changes
    setCars((prevCars) =>
      prevCars.map((car) =>
        car.id === carId ? { ...car, ...updatedCar } : car
      )
    );
  } catch (error) {
    console.error("Error updating car:", error);
  }
};
  const handleDelete = (carId) => {
    // Delete the car on the server
    fetch(`http://127.0.0.1:5000/api/cars/${carId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error deleting car:", error));

    // Update the local state to reflect the deletion
    setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCar((prevNewCar) => ({ ...prevNewCar, [name]: value }));
  };
  const handleAddCar = () => {
    // Add the new car on the server
    fetch("http://127.0.0.1:5000/api/cars", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: newCar.name,
        model: newCar.model,
        type: newCar.type,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error adding car:", error));

    // Update the local state to reflect the addition
    setCars((prevCars) => [...prevCars, newCar]);

    // Clear the form after adding the car
    setNewCar({
      name: "",
      model: "",
      type: "",
    });
  };

  if (!cars || cars.length === 0) {
    return <div>Ni podatkov</div>;
  }

  return (
    <main>
      <div className="container" style={{ textAlign: "center" }}>
        <br></br>
        <h1 className="test text-center">
          Administracijska stran za prodajo avtomobilov
        </h1>
        <br></br>
        <form>
          <div className="mb-3">
            <label className="form-label">Name:</label>
            <input
              type="text"
              className="form-control"
              name="name"
              value={newCar.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Model:</label>
            <input
              type="text"
              className="form-control"
              name="model"
              value={newCar.model}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Type:</label>
            <select
              className="form-control"
              name="type"
              value={newCar.type}
              onChange={handleInputChange}
              required
            >
              <option value="" disabled>
                Izberi tip vozila
              </option>
              {tipCars.map((tipcar) => (
                <option key={tipcar.id} value={tipcar.nametip}>
                  {tipcar.nametip}
                </option>
              ))}
            </select>
          </div>
          <button
            className={`btn ${
              newCar.name && newCar.model && newCar.type
                ? "btn-success"
                : "btn-secondary"
            }`}
            onClick={handleAddCar}
            disabled={!newCar.name || !newCar.model || !newCar.type}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="16"
              width="14"
              viewBox="0 0 448 512"
            >
              <path d="M256 80c0-17.7-14.3-32-32-32s-32 14.3-32 32V224H48c-17.7 0-32 14.3-32 32s14.3 32 32 32H192V432c0 17.7 14.3 32 32 32s32-14.3 32-32V288H400c17.7 0 32-14.3 32-32s-14.3-32-32-32H256V80z" />
            </svg>
          </button>
        </form>


        <table className="table table-striped">
          <thead>
            <tr>
              <th>Name</th>
              <th>Model</th>
              <th>Tip</th>
              <th>Akcija</th>
            </tr>
          </thead>
          <tbody>
            {cars.map((car, index) => (
              <Car
                key={index}
                car={car}
                onUpdate={handleUpdate}
                onDelete={handleDelete}
              />
            ))}
          </tbody>
        </table>
      </div>

    </main>
  );
}

export default App;
