import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import Car from "./Car";

function App() {
  const [cars, setCars] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/")
      .then((res) => res.json())
      .then((data) => setCars(data))
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleUpdate = (carId, updatedCar) => {
    // Update the car on the server
    fetch(`http://127.0.0.1:5000/cars/${carId}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(updatedCar),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error updating car:", error));

    // Update the local state to reflect the changes
    setCars((prevCars) =>
      prevCars.map((car) =>
        car.id === carId ? { ...car, ...updatedCar } : car
      )
    );
  };

  const handleDelete = (carId) => {
    // Delete the car on the server
    fetch(`http://127.0.0.1:5000/cars/${carId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error deleting car:", error));

    // Update the local state to reflect the deletion
    setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
  };

  if (!cars || cars.length === 0) {
    return <div>Ni podatkov</div>;
  }

  return (
    <main>

      <div className="container">
        <br></br>
        <h1 className="text-center" >Spletna stran za prodajo avtomobilov</h1>
        <br></br>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Name</th>
              <th>Model</th>
              <th>Tip</th>
              <th>Akcija</th>
            </tr>
          </thead>
          <tbody>
            {cars.map((car, index) => (
              <Car
                key={index}
                car={car}
                onUpdate={handleUpdate}
                onDelete={handleDelete}
              />
            ))}
          </tbody>
        </table>
      </div>
    </main>
  );
}

export default App;
