import React, { useState } from "react";

const Car = ({ car, onUpdate, onDelete }) => {
  const [updatedCar, setUpdatedCar] = useState({ name: "", model: "", type: "" });
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedCar((prevCar) => ({ ...prevCar, [name]: value }));
    
    // Open the modal when input is changed
    setIsModalOpen(true);
  };

  const handleUpdate = () => {
    onUpdate(car.id, updatedCar);
    
    // Clear the input fields after update
    setUpdatedCar({ name: "", model: "", type: "" });
    
    // Close the modal after update
    setIsModalOpen(false);
  };

  const handleCloseModal = () => {
    // Close the modal without updating
    setIsModalOpen(false);
  };

  return (
    <tr>
      <td>{car.name}</td>
      <td>{car.model}</td>
      <td>{car.type}</td>
      <td>
        <div className="d-flex">
          <div className="mr-2">
            <input
              type="text"
              name="name"
              value={updatedCar.name}
              onChange={handleInputChange}
              placeholder="New Name"
            />
          </div>
          <div className="mr-2">
            <input
              type="text"
              name="model"
              value={updatedCar.model}
              onChange={handleInputChange}
              placeholder="New Model"
            />
          </div>
          <div className="mr-2">
            <input
              type="text"
              name="type"
              value={updatedCar.type}
              onChange={handleInputChange}
              placeholder="New Type"
            />
          </div>
          <button className="btn btn-warning mr-2" onClick={handleUpdate}>
            Update
          </button>
          <button className="btn btn-danger" onClick={() => onDelete(car.id)}>
            Delete
          </button>
        </div>
        
        {/* Modal */}
        {isModalOpen && (
          <div className="modal">
            <div className="modal-content">
              <span className="close" onClick={handleCloseModal}>&times;</span>
              <p>This is a modal. You can customize its content.</p>
            </div>
          </div>
        )}
      </td>
    </tr>
  );
};

export default Car;
