import { useEffect, useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import Car from "./Car";
import "./App.css";

function App() {
  const [cars, setCars] = useState([]);
  const [tipCars, setTipCars] = useState([]);
  const [newCar, setNewCar] = useState({
    name: "",
    model: "",
    type: "",
  });
  const [editCar, setEditCar] = useState(null); // State for editing
  const [modal, setModal] = useState(false);
  const [modalFormData, setModalFormData] = useState({
    name: "",
    model: "",
    type: "",
  });

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/cars")
      .then((res) => res.json())
      .then((data) => {
        setCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/api/tip")
      .then((res) => res.json())
      .then((data) => {
        setTipCars(data);
      })
      .catch((error) => console.error("Error:", error));
  }, []);

  const handleDelete = (carId) => {
    // Delete the car on the server
    fetch(`http://127.0.0.1:5000/api/cars/${carId}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error deleting car:", error));

    // Update the local state to reflect the deletion
    setCars((prevCars) => prevCars.filter((car) => car.id !== carId));
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewCar((prevNewCar) => ({ ...prevNewCar, [name]: value }));
  };

  const handleAddCar = () => {
    // Add the new car on the server
    fetch("http://127.0.0.1:5000/api/cars", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: newCar.name,
        model: newCar.model,
        type: newCar.type,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error adding car:", error));

    // Update the local state to reflect the addition
    setCars((prevCars) => [...prevCars, newCar]);

    // Clear the form after adding the car
    setNewCar({
      name: "",
      model: "",
      type: "",
    });

    // Close the modal
    toogleModal();
  };

  const handleEdit = (car) => {
    // Set the car data to editCar state
    setEditCar(car);

    // Set the modal form data
    setModalFormData({
      name: car.name,
      model: car.model,
      type: car.type,
    });

    // Open the modal for editing
    toogleModal();
  };

  const handleEditInputChange = (e) => {
    const { name, value } = e.target;
    setModalFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const toogleModal = () => {
    // Toggle the modal state
    setModal((prevModal) => !prevModal);
  };
  const handleUpdateCar = () => {
    // Update the car on the server
    fetch(`http://127.0.0.1:5000/api/cars/${editCar.id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        name: modalFormData.name,
        model: modalFormData.model,
        type: modalFormData.type,
      }),
    })
      .then((res) => res.json())
      .then((data) => console.log(data))
      .catch((error) => console.error("Error updating car:", error));

    // Update the local state to reflect the changes
    setCars((prevCars) =>
      prevCars.map((car) =>
        car.id === editCar.id
          ? { ...car, ...modalFormData }
          : car
      )
    );

    // Close the modal
    toogleModal();
  };

  if (!cars || cars.length === 0) {
    return <div>Ni podatkov</div>;
  }
  return (
    <main>
      <div className="container" style={{ textAlign: "center" }}>
        <br />
        <h1 className="test text-center">
          Administracijska stran za prodajo avtomobilov
        </h1>
        <br />
        <form>
          <div className="mb-3">
            <label className="form-label">Name:</label>
            <input
              type="text"
              className="form-control"
              name="name"
              value={newCar.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Model:</label>
            <input
              type="text"
              className="form-control"
              name="model"
              value={newCar.model}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Type:</label>
            <select
              className="form-control"
              name="type"
              value={newCar.type}
              onChange={handleInputChange}
              required
            >
              <option value="" disabled>
                Izberi tip vozila
              </option>
              {tipCars.map((tipcar) => (
                <option key={tipcar.id} value={tipcar.nametip}>
                  {tipcar.nametip}
                </option>
              ))}
            </select>
          </div>
          <button
            className={`btn ${
              newCar.name && newCar.model && newCar.type
                ? "btn-success"
                : "btn-secondary"
            }`}
            onClick={handleAddCar}
            disabled={!newCar.name || !newCar.model || !newCar.type}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="16"
              width="14"
              viewBox="0 0 448 512"
            >
